<script>
import gql from 'graphql-tag';

export default {
  name: 'Hero',

  apollo: {
    home: {
      query: gql`{
        home(locale: en) {
          heading1
          heading2A
          heading2B
          heading2C
          heroDescription
          heroPicture {
            url
            alt
          }
          aboutHeading
          aboutDescription
        }
      }`,
      prefetch: true
    }
  },


  computed: {
    // isHome() {
    //   return this.$route.path === '/';
    // },

    heading1() {
      if (this.home) {
        return this.home.heading1.split(' ');
      }

      return [];
    }
  },

  methods: {
    figureVisibilityChanged: function (isVisible, entry) {
      isVisible ? entry.target.classList.add('visible') : entry.target.classList.remove('visible');
      // console.log(entry)
    }
  }
};
</script>
